// prevent default on all <a> tags with an ng-click event
// from http://stackoverflow.com/questions/10931315/how-to-preventdefault-on-anchor-tags-in-angularjs
lolPlaygroundApp.directive('a', function() {
    return {
        restrict: 'E',
        link: function(scope, elem, attrs) {
            if(attrs.ngClick || attrs.href === '' || attrs.href === '#'){
                elem.on('click', function(e){
                    e.preventDefault();
                });
            }
        }
   };
});

lolPlaygroundApp.filter('championFilter', function() {
	return function(champions,filters) {
		if(filters.length == 0)
			return champions;
		
		var newChampions = [];
		var i, j, champion, filter, value, includeChampion;
		
		for(i in champions)
		{
			champion = champions[i];
			
			for(j in filters)
			{
				filter = filters[j];
				
				switch(filter.field)
				{
					case 'health': value = champion.health(); break;
					case 'mana': value = champion.mana(); break;
					case 'attack_damage': value = champion.attack_damage(); break;
					case 'attack_speed': value = champion.attack_speed(); break;
					case 'range': value = champion.range(); break;
					case 'armor': value = champion.armor(); break;
					case 'magic_res': value = champion.magic_res(); break;
					case 'move_speed': value = champion.move_speed(); break;
				}
				
				switch(filter.comparison)
				{
					case 'gte':
						includeChampion = (value >= filter.value);
						break;
						
					case 'gt':
						includeChampion = (value > filter.value);
						break;
						
					case 'lte':
						includeChampion = (value <= filter.value);
						break;
						
					case 'lt':
						includeChampion = (value < filter.value);
						break;
						
					case 'eq':
						includeChampion = (value == filter.value);
						break;
				}
				
				if(!includeChampion)
					break;
			}
			
			if(includeChampion)
				newChampions.push(champion);
		}
		
		return newChampions;
	};
});

lolPlaygroundApp.controller('lolSearchCtrl', function lolSearchCtrl($scope) {

	$scope.championData = [];
	
	var i, j, champion, attackSpeed;

	$scope.championSort = 'name';
	$scope.sortReversed = false;
	$scope.filters = [];
	$scope.level = 1;

	$.each(window.championData, function(i, champion) {
		$scope.championData.push({
			name: champion.name,
			health: function() { return Math.round(champion.health * 100 + champion.health_per_level * ($scope.level - 1) * 100) / 100; },
			mana: function() { return Math.round(champion.mana * 100 + champion.mana_per_level * ($scope.level - 1) * 100) / 100; },
			attack_damage: function() { return Math.round(champion.attack_damage * 100 + champion.attack_damage_per_level * ($scope.level - 1) * 100) / 100; },
			attack_speed: function() { return Math.round(champion.attack_speed * (1 + ((champion.attack_speed_per_level / 100) * ($scope.level - 1))) * 10000) / 10000; },
			range: function() { return champion.range; },
			armor: function() { return Math.round(champion.armor * 100 + champion.armor_per_level * ($scope.level - 1) * 100) / 100; },
			magic_res: function() { return Math.round(champion.magic_res * 100 + champion.magic_res_per_level * ($scope.level - 1) * 100) / 100; },
			move_speed: function() { return champion.move_speed; }
		});
	});
	
	$scope.addFilter = function() {
		$scope.filters.push({
			field: 'health',
			comparison: 'gte',
			value: 0
		});
	};
	
	$scope.removeFilter = function(index) {
		if(index >= 0 && index < $scope.filters.length)
			$scope.filters.splice(index, 1);
	};
	
	$scope.sortBy = function(field) {
		$scope.sortReversed = ($scope.championSort === field) ? !$scope.sortReversed : false;
		$scope.championSort = field;
	};
	
});